import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ItineraryDetail = () => {
  const { slug } = useParams();
  const [itinerary, setItinerary] = useState(null);

  useEffect(() => {
    axios
      .get(`http://127.0.0.1:8000/api/itineraries/${slug}/`)
      .then((response) => setItinerary(response.data))
      .catch((error) => console.error("Error fetching itinerary:", error));
  }, [slug]);

  if (!itinerary) return <p>Loading itinerary details...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{itinerary.title}</h2>
      <p><strong>Destination:</strong> {itinerary.destination}</p>
      <p><strong>Duration:</strong> {itinerary.duration} days</p>
      <p><strong>Price:</strong> â‚¹ {itinerary.price}</p>
      <p>{itinerary.description}</p>
      {itinerary.qr_code && (
        <img src={`http://127.0.0.1:8000${itinerary.qr_code}`} alt="QR Code" />
      )}
    </div>
  );
};

export default ItineraryDetail;
