import React, { useState } from "react";
import axios from "axios";

const ReelUpload = ({ itineraryId }) => {
  const [videoFile, setVideoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState("");

  const handleFileChange = (e) => {
    setVideoFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!videoFile) {
      setMessage("Please select a video file.");
      return;
    }

    const formData = new FormData();
    formData.append("itinerary", itineraryId);
    formData.append("video", videoFile);

    try {
      setUploading(true);
      await axios.post("http://127.0.0.1:8000/api/reels/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setMessage("Reel uploaded successfully!");
      setVideoFile(null);
    } catch (error) {
      console.error("Upload failed:", error);
      setMessage("Failed to upload reel.");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <h4>Upload a New Reel</h4>
      <form onSubmit={handleUpload}>
        <input type="file" accept="video/*" onChange={handleFileChange} />
        <button type="submit" disabled={uploading} style={{ marginLeft: "10px" }}>
          {uploading ? "Uploading..." : "Upload Reel"}
        </button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ReelUpload;
