import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import ItineraryList from "./components/ItineraryList";
import ItineraryDetail from "./components/ItineraryDetail";

function App() {
  // Toggle mobile menu
  const toggleMenu = () => {
    const menu = document.getElementById("menu");
    menu.classList.toggle("hidden");
  };

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
        {/* Navigation Bar */}
        <nav className="bg-white dark:bg-gray-800 shadow-md p-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold">
            <Link to="/">HOLIDAYQR</Link>
          </h1>

          <div className="md:hidden">
            <button
              id="menu-toggle"
              className="text-gray-700 dark:text-white focus:outline-none"
              onClick={toggleMenu}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>

          <ul id="menu" className="hidden md:flex space-x-4">
            <li><Link to="/" className="hover:text-blue-500">Home</Link></li>
            <li><Link to="/upload" className="hover:text-blue-500">Upload Reel</Link></li>
            <li><Link to="/profile" className="hover:text-blue-500">Profile</Link></li>
            <li><Link to="/support" className="hover:text-blue-500">Support</Link></li>
          </ul>
        </nav>

        {/* Main Content */}
        <main className="p-4">
          <Routes>
            <Route path="/" element={<ItineraryList />} />
            <Route path="/itinerary/:slug" element={<ItineraryDetail />} />
            {/* Future routes: UploadReel, Profile, Support */}
          </Routes>
        </main>

        {/* Dark Mode Toggle Button */}
        <div className="fixed bottom-4 right-4 z-50">
          <button
            className="bg-gray-800 text-white px-4 py-2 rounded-full shadow-lg hover:bg-gray-700"
            onClick={() => document.documentElement.classList.toggle("dark")}
          >
            Toggle Dark Mode
          </button>
        </div>
      </div>
    </Router>
  );
}

export default App;
